import React, { Component } from 'react'
import axios from 'axios'
import moment from 'moment'
import DateTimePicker from 'react-datetime-picker';

class AddAppointment extends Component {
    constructor(props) {
        super(props);
        this.state = { Title: '', Start: '', End: '' };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log(this.state.Start)
        var m = moment(this.state.Start)
        console.log(m) 
        const appointment = {
            Name: this.state.Title,
            Start: this.state.Start,
            End: this.state.End
        };
        const url = 'https://noname.lab.medsolv.net/shifts';
        axios.post(url, appointment);
        console.log('Termin wurde hinzugef端gt');
        console.log(url, appointment);
        this.setState({
            Title: '', Start: '', End: ''
        })
        this.props.parentMethod();
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Einen Termin hinzuf端gen: </label>
                    <input name="Title" type="text" onChange={this.handleChange} value={this.state.Title} placeholder="Name der Schicht" />
                    <DateTimePicker name="Start" onChange={this.handleChange} value={this.state.Start} />
                    <DateTimePicker name="End" onChange={this.handleChange} value={this.state.End} />
                    <button type="submit">Senden</button>
                </form>
            </div>
        )
    }
}

export default AddAppointment
