import React, { Component } from 'react'
import axios from 'axios';
import moment from 'moment';

class AddAppointment extends Component {
    constructor(props) {
        super(props);
        this.state = { Title: '', Start: '', End: ''};
        this.handleTitleChange = this.handleTitleChange.bind(this);
        this.handleStartChange = this.handleStartChange.bind(this);
        this.handleEndChange = this.handleEndChange.bind(this);
    }

    handleTitleChange(event) {
        this.setState({ Title: event.target.value,  });
    }
    handleStartChange(event) {
        this.setState({ Start: event.target.value,  });
    }
    handleEndChange(event) {
        this.setState({ End: event.target.value,  });
    }
    
    handleSubmit = (e) => {
        e.preventDefault();
        const appointment = {
            Name: this.state.Title,
            Start: moment(this.state.Start).format(),
            End: moment(this.state.End).format()
        };
        const url = 'https://noname.lab.medsolv.net/shifts';
        axios.post(url, appointment);
        console.log(url, appointment);
        // hier wird der Placeholder zur端ckgesetzt
        e.target.reset()
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Einen Termin hinzuf端gen: </label>
                    <input id="Title" type="text" onChange={this.handleChange} value={this.state.Title} placeholder="Name der Schicht" />
                    <input id="Start" type="text" onChange={this.handleChange} value={this.state.Start} placeholder="Start Bsp.: 2018-12-12 10:30:48" />
                    <input id="End" type="text" onChange={this.handleChange} value={this.state.End} placeholder="Ende Bsp.: 2018-12-12 11:30:48" />
                    <button type="submit">Senden</button>
                </form>
            </div>
        )
    }
}

export default AddAppointment
