import React, { Component } from 'react'

class AddAppointment extends Component {
  state = {
    content: ''
  }
  handleChange = (e) => {
    this.setState({
      content: e.target.value
    });
  }
  handleSubmit = (e) => {
    e.preventDefault();
    // call function to add a todo
    this.props.AddAppointment(this.state);
    this.setState({
      content: ''
    })
  }
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>Einen Termin hinzufügen:</label>
          <input id="SubmitTitle" type="text" onChange={this.handleChange} value={this.state.content} placeholder="Titel"/>
          <input id="SubmitStart" type="text" onChange={this.handleChange} value={this.state.content} placeholder="Start"/>
          <input id="SubmitEnde" type="text" onChange={this.handleChange} value={this.state.content} placeholder="Ende"/>
        </form>
      </div>
    )
  }
}

export default AddAppointment
