import { render } from 'react-dom';
import './index.css';
import * as React from 'react';
import { ScheduleComponent, ViewsDirective, ViewDirective, Day, Week, WorkWeek, Month, Agenda, Inject, Resize, DragAndDrop } from '@syncfusion/ej2-react-schedule';
// import { scheduleData } from './datasource';
import { extend } from '@syncfusion/ej2-base';
// import { DatePickerComponent } from '@syncfusion/ej2-react-calendars';
import { PropertyPane } from './property-pane';
import AddAppointment from './AddAppointment';
/**
 * Schedule Default sample
 */

export class Default extends React.Component {
  constructor(props) {
    super(props);
    this.data = extend([], null, true);
  }

  state = {
    scheduleData: []
  }

  change(args) {
    this.scheduleObj.selectedDate = args.value;
    this.scheduleObj.dataBind();
  }

handleChange = (e) => {

}

componentDidMount () {
  fetch('https://noname.lab.medsolv.net/Shifts')
      .then((response) => {
        return response.json();
      })
      .then((myJson) => {
        // window.appointments = myJson;
        let scheduleData = myJson.map(shift => ({ Id: shift.Id, Subject: shift.Name, StartTime: shift.Start, EndTime: shift.End }));
        this.setState({scheduleData})
        console.log(scheduleData);
      });
}

  render() {
    // console.log(this.state.scheduleData);
    return (
      <div className='schedule-control-section'>
        <div className='col-lg-9 control-section'>
          <div className='control-wrapper'>
            <ScheduleComponent height='650px' ref={scheduleData => this.scheduleObj = scheduleData} selectedDate={new Date(2018, 12, 12)} eventSettings={{ dataSource: this.data }}>
              <ViewsDirective>
                <ViewDirective option='Day' />
                <ViewDirective option='Week' />
                <ViewDirective option='WorkWeek' />
                <ViewDirective option='Month' />
                <ViewDirective option='Agenda' />
              </ViewsDirective>
              <Inject services={[Day, Week, WorkWeek, Month, Agenda, Resize, DragAndDrop]} />
            </ScheduleComponent>
          </div>
        </div>
        <AddAppointment AddAppointment={this.AddAppointment} />
        <br />
        <button>POST</button>
        <br />
        <button>DELETE</button>
        <br />
        <div className='col-lg-3 property-section'>
          <PropertyPane title='Properties'>
            <table id='property' title='Properties' className='property-panel-table' style={{ width: '100%' }}>
              <tbody>
                <tr style={{ height: '50px' }}>
                  <td style={{ width: '30%' }}>
                    <div className='col-md-4' style={{ paddingTop: '8px' }}>Current Date</div>
                  </td>
                  <td style={{ width: '70%' }}>
                    {/* <div className='datepicker-control-section'>
                      <DatePickerComponent value={new Date(2018, 12, 12)} change={this.change.bind(this)}></DatePickerComponent>
                    </div> */}
                  </td>
                </tr>
              </tbody>
            </table>
          </PropertyPane>
        </div>
      </div>);
  }
}

render(<Default />, document.getElementById('sample'));
