import React, { Component } from 'react'
import axios from 'axios';

class DeleteAppointment extends Component {
    state = {
        content: ''
    }

    handleChange = (e) => {
        this.setState({
            content: e.target.value
        });
    }

    // call function to delete a shift
    handleSubmit = (e) => {
        e.preventDefault();
        const id = document.getElementById("Id").value
        const url = 'https://noname.lab.medsolv.net/shifts/'
        axios.delete(url + id)
        console.log(e.target.value);
        console.log(url);
        // hier wird der Placeholder zurückgesetzt
        e.target.reset()
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Einen Termin löschen: </label>
                    <input id="Id" type="text" onChange={this.handleChange} value={this.state.Id} placeholder="ID der Schicht" />
                    <button type="submit">Senden</button>
                </form>
            </div>
        )
    }
}

export default DeleteAppointment
