import { render } from 'react-dom';
import React from 'react';
import './index.css';
// import * as React from 'react';
import {
	ScheduleComponent,
	Day,
	Week,
	WorkWeek,
	Month,
	Agenda,
	Inject,
	Resize,
	// ViewsDirective,
	// ViewDirective,
	// ResourcesDirective,
	// ResourceDirective,
	DragAndDrop
} from '@syncfusion/ej2-react-schedule';
import { extend } from '@syncfusion/ej2-base';
import { DateTimePickerComponent } from '@syncfusion/ej2-react-calendars';
import { DropDownListComponent } from '@syncfusion/ej2-react-dropdowns';
// import { DataManager, Query } from '@syncfusion/ej2-data';
import AddShifts from './shifts/AddShifts';
import DeleteShifts from './shifts/DeleteShifts';
import ModifyShifts from './shifts/ModifyShifts';
import AddShiftEntry from './shiftEntry/AddShiftEntry';
import DeleteShiftEntry from './shiftEntry/DeleteShiftEntry';
import ModifyShiftEntry from './shiftEntry/ModifyShiftEntry';

import Modal from './Modal';

class Default extends React.Component {
	constructor(...props) {
		console.log(props);
		super(props);
		this.data = extend([], null, true);
		this.state = { isOpen: false, scheduleData: [], scheduleShiftEntry: [] };
		// args = extend(...args)
	}

	// state = {
	// 	scheduleData: [],
	// 	scheduleShiftEntry: []
	// };

	change(args) {
		this.scheduleObj.selectedDate = args.value;
		this.scheduleObj.dataBind();
	}

	getData = () => {
		fetch('http://192.168.30.110:1337/Shifts')
			.then((response) => {
				return response.json();
			})
			.then((myJson) => {
				let scheduleData = myJson.map((shift) => ({
					Id: shift.id,
					Subject: shift.name,
					StartTime: shift.start,
					EndTime: shift.end
				}));
				this.setState({ scheduleData });
				// console.log(this);
			});
	};

	getShiftEntry = () => {
		fetch('http://192.168.30.110:1337/ShiftEntry')
			.then((response) => {
				return response.json();
			})
			.then((myJson) => {
				let scheduleShiftEntry = myJson.map((shiftEntry) => ({
					Id: shiftEntry.id,
					Subject: 'Resource',
					StartTime: shiftEntry.start,
					EndTime: shiftEntry.end,
					CategoryColor: '#404040'
				}));
				this.setState({ scheduleShiftEntry });
			});
	};

	componentDidMount() {
		this.getData();
		this.getShiftEntry();
	}

	onEventRendered(args) {
		let categoryColor = args.data.CategoryColor;
		if (!args.element || !categoryColor) {
			return;
		}
		if (this.currentView === 'Agenda') {
			args.element.firstChild.style.borderLeftColor = categoryColor;
		} else {
			args.element.style.backgroundColor = categoryColor;
		}
	}

	// wird ausgelÃ¶st wenn ein bestehendes appointment geklickt wird
	// onEventClick(args) {
	// 	console.log('onEventClick args --->', args);
	// }

	onPopupOpen(args) {
		console.log('onPopupOpen--->', args.element);
		debugger;
		if (args.type === 'Editor') {
			let statusElement = args.element.querySelector('#EventType');
			statusElement.setAttribute('name', 'EventType');
			console.log('onPopupOpen statusElement--->', statusElement);
		}
	}

	onActionBegin(args) {
		console.log('onActionBegin', args);
		if (args.requestType === 'eventCreate') {
			let data = args.data;
			if (!this.combinedSources.isSlotAvailable(data.StartTime, data.EndTime)) {
				args.cancel = true;
			}
		}
	}

	toggleModal = () => {
		console.log('toggleModal has been triggered!')
		this.setState({
			isOpen: !this.state.isOpen
		});
	};

	// editorTemplate(props) {
	// 	console.log('editorTemplate--->', props);
	// 	return (
	// 		<table className="custom-event-editor" style={{ width: '100%', cellpadding: '5' }}>
	// 			<tbody>
	// 				<tr>
	// 					<td className="e-textlabel">Summary</td>
	// 					<td style={{ colspan: '4' }}>
	// 						<input
	// 							id="Summary"
	// 							className="e-field e-input"
	// 							type="text"
	// 							name="Subject"
	// 							style={{ width: '100%' }}
	// 						/>
	// 					</td>
	// 				</tr>
	// 				<tr>
	// 					<td className="e-textlabel">Status</td>
	// 					<td style={{ colspan: '4' }}>
	// 						<DropDownListComponent
	// 							id="EventType"
	// 							placeholder="Choose status"
	// 							className="e-field"
	// 							style={{ width: '100%' }}
	// 							dataSource={[ 'New', 'Requested', 'Confirmed' ]}
	// 						/>
	// 					</td>
	// 				</tr>
	// 				<tr>
	// 					<td className="e-textlabel">From</td>
	// 					<td style={{ colspan: '4' }}>
	// 						<DateTimePickerComponent id="StartTime" className="e-field" />
	// 					</td>
	// 				</tr>
	// 				<tr>
	// 					<td className="e-textlabel">To</td>
	// 					<td style={{ colspan: '4' }}>
	// 						<DateTimePickerComponent id="EndTime" className="e-field" />
	// 					</td>
	// 				</tr>
	// 				<tr>
	// 					<td className="e-textlabel">Reason</td>
	// 					<td style={{ colspan: '4' }}>
	// 						<textarea
	// 							id="Description"
	// 							className="e-field e-input"
	// 							name="Description"
	// 							rows={3}
	// 							cols={50}
	// 							style={{ width: '100%', height: '60px !important', resize: 'vertical' }}
	// 						/>
	// 					</td>
	// 				</tr>
	// 			</tbody>
	// 		</table>
	// 	);
	// }

	render() {
		var combinedSources = [ ...this.state.scheduleData, ...this.state.scheduleShiftEntry ];
		console.log('this.state->', this.state);
		// console.log('this.scheduleObj->', this.scheduleObj);
		// console.log('this.state.scheduleData->', this.state.scheduleData)
		// console.log('this.state.scheduleShiftEntry->', this.state.scheduleShiftEntry)
		// console.log('combinedSources-->', combinedSources)
		return (
			<div className="schedule-control-section">
				<div className="col-lg-9 control-section">
					<div className="control-wrapper">
						<div>
							<div className="App">
								<button onClick={this.toggleModal.bind(this)}>Open the modal</button>
								<Modal show={this.state.isOpen} onClose={this.toggleModal}>
									Here's some content for the modal
								</Modal>
							</div>
						</div>
						<div>
							<AddShifts parentMethod={this.getData}>{this.props.children}</AddShifts>
							<br />
							<ModifyShifts parentMethod={this.getData}>{this.props.children}</ModifyShifts>
							<br />
							<DeleteShifts parentMethod={this.getData}>{this.props.children}</DeleteShifts>
							<br />
						</div>
						<div>
							<ScheduleComponent
								height="650px"
								width="1000px"
								eventSettings={{ dataSource: this.state.scheduleData }}
							>
								<Inject services={[ Day, Week, WorkWeek, Month, Agenda, Resize, DragAndDrop ]} />
							</ScheduleComponent>
						</div>
						<div>
							<AddShiftEntry parentMethod={this.getShiftEntry}>{this.props.children}</AddShiftEntry>
							<br />
							<ModifyShiftEntry parentMethod={this.getShiftEntry}>
								{this.props.children}>
							</ModifyShiftEntry>
							<br />
							<DeleteShiftEntry parentMethod={this.getShiftEntry}>{this.props.children}</DeleteShiftEntry>
							<br />
						</div>
						<div>
							<ScheduleComponent
								cssClass="custom-work-days"
								height="650px"
								width="1000px"
								eventSettings={{
									dataSource: combinedSources
								}}
								eventRendered={this.onEventRendered.bind(this)}
								/*renderCell={this.onRenderCell.bind(this)}*/
							>
								<Inject services={[ Day, Week, WorkWeek, Month, Agenda, Resize, DragAndDrop ]} />
							</ScheduleComponent>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

render(<Default />, document.getElementById('sample'));
