import React, { Component } from 'react'
import axios from 'axios';
import moment from 'moment';

class ModifyAppointment extends Component {
    constructor() {
        super();
        this.state = { Title: '', Start: '', End: ''};
        this.handleChange = this.handleChange.bind(this);
        // this.fileInput = React.createRef();
    }

    handleChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }
        
    handleSubmit = (e) => {
        e.preventDefault();
        const id = this.state.Id
        const Name = this.state.Title
        const Start = moment(this.state.Start).format()
        const End = moment(this.state.End).format()
        const appointment= (Name + Start + End)
        const url = 'https://noname.lab.medsolv.net/shifts/';
        axios.patch(url + id, appointment);
        console.log('Termin wurde verändert');
        console.log(url + id, appointment);
        // hier wird der Placeholder zurückgesetzt
        e.target.reset()
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Einen Termin verändern: </label>
                    <input id="Id" type="text" onChange={this.handleChange} value={this.state.Id} placeholder="ID der Schicht" />
                    <input id="Title" type="text" onChange={this.handleChange} value={this.state.Title} placeholder="Name der Schicht" />
                    <input id="Start" type="text" onChange={this.handleChange} value={this.state.Start} placeholder="Start Bsp.: 2018-12-12 10:30:48" />
                    <input id="End" type="text" onChange={this.handleChange} value={this.state.End} placeholder="Ende Bsp.: 2018-12-12 11:30:48" />
                    <button type="submit">Senden</button>
                </form>
            </div>
        )
    }
}

export default ModifyAppointment
